import React, { useState, useEffect } from 'react';
import Die from './components/Die.jsx';
import ScoreBoard from './components/ScoreBoard.jsx';
import { nanoid } from 'nanoid';

function App() {
  const [dice, setDice] = useState(allNewDice());
  const [score, setScore] = useState(0);
  const [tenzies, setTenzies] = useState(false);
  const [scoreBoard, setScoreBoard] = useState(
    () => JSON.parse(localStorage.getItem('scores')) || []
  );

  useEffect(() => {
    localStorage.setItem('scores', JSON.stringify(scoreBoard));
  }, [scoreBoard]);

  useEffect(() => {
    let dupe = 0;
    for (let i = 0; i < dice.length; i++) {
      if (dice[i].isHeld) {
        dupe += 1;
      }
    }
    console.log(dupe);
    dupe === 10 ? setTenzies(true) : setTenzies(false);
    console.log(tenzies);
  }, [dice]);

  function allNewDice() {
    const numArr = [];
    for (let i = 0; i < 10; i++) {
      numArr.push({
        value: Math.ceil(Math.random() * 6),
        isHeld: false,
        id: nanoid(),
      });
    }
    return numArr;
  }

  function reroll() {
    if (!tenzies) {
      setScore((prevScore) => prevScore + 1);
      setDice((prevDice) =>
        prevDice.map((die) =>
          die.isHeld ? die : { ...die, value: Math.ceil(Math.random() * 6) }
        )
      );
    } else {
      setScore(0);
      setDice(allNewDice());
    }
  }

  function holdDie(id) {
    setDice((prevDice) =>
      prevDice.map((die) =>
        die.id === id ? { ...die, isHeld: !die.isHeld } : die
      )
    );
  }

  const styles = {
    height: tenzies ? '800px' : '400px',
  };

  return (
    <div>
      <h1 className='title'>Tenzies</h1>
      <main className='main' style={styles}>
        <p className='instructions'>
          Roll until all dice are the same. Click each die to freeze it at its
          current value between rolls.
        </p>
        <div className='dice'>
          {dice.map((die) => (
            <Die
              isHeld={die.isHeld}
              value={die.value}
              key={die.id}
              holdDie={() => holdDie(die.id)}
            />
          ))}
        </div>
        <button onClick={() => reroll()} className='roll-dice'>
          {tenzies ? 'Play Again?' : 'Roll'}
        </button>
        <div className='score'>
          <h4>{tenzies ? 'Final ' : ''}Score</h4>
          <h3>{score}</h3>
        </div>
      </main>
      {tenzies && <ScoreBoard scoreBoard={scoreBoard} />}
    </div>
  );
}

export default App;
