import React, { useState, useEffect } from 'react';
import Die from './components/Die.jsx';

function App() {
  const [dice, setDice] = useState(allNewDice());

  function allNewDice() {
    const numArr = [];
    for (let i = 0; i < 10; i++) {
      numArr.push(Math.floor(Math.random() * 6) + 1);
    }
    return numArr;
  }

  function reroll() {
    setDice(allNewDice());
    console.log('clicked');
  }

  return (
    <main className='main'>
      <div className='dice'>
        {dice.map((die, i) => (
          <Die value={die} key={i} />
        ))}
      </div>
      <button onClick={() => reroll} className='roll-button'>
        Roll
      </button>
    </main>
  );
}

export default App;
