import React, { useState, useEffect } from 'react';
import Die from './components/Die.jsx';

function App() {
  const [myDice, setMyDice] = useState(allNewDice());

  function allNewDice() {
    const numArr = [];
    for (let i = 0; i < 10; i++) {
      numArr.push(Math.floor(Math.random() * 6) + 1);
    }
    return numArr;
  }

  console.log(myDice);

  return (
    <main className='main'>
      <div className='dice'>
        {myDice.map((die, i) => (
          <Die value={die} key={i} />
        ))}
      </div>
    </main>
  );
}

export default App;
