import React, { useState, useEffect } from 'react';
import Die from './components/Die.jsx';

function App() {
  const [dice, setDice] = useState(allNewDice());

  function allNewDice() {
    const numArr = [];
    for (let i = 0; i < 10; i++) {
      numArr.push({ value: Math.floor(Math.random() * 6) + 1, isHeld: false });
    }
    return numArr;
  }

  function reroll() {
    setDice(allNewDice());
  }

  function holdDie() {
    setDice((prevDice) => ({ ...prevDice, isHeld: true }));
    console.log(dice);
  }

  return (
    <main className='main'>
      <div className='dice'>
        {dice.map((die, i) => (
          <Die isHeld={die.isHeld} value={die.value} key={i} />
        ))}
      </div>
      <button onClick={() => reroll()} className='roll-dice'>
        Roll
      </button>
    </main>
  );
}

export default App;
