import React, { useState, useEffect } from 'react';
import Die from './components/Die.jsx';

function App() {
  const [randomNum, setRandomNum] = useState(
    Math.floor(Math.random() * 10) + 1
  );
  const [clicked, setClicked] = useState(false);
  const [numArr, setNumArr] = useState([]);

  function handleClicked() {
    console.log('clicked');
    setClicked((prevClicked) => !prevClicked);
  }

  useEffect(() => {
    const myArr = [];
    if (!numArr[0]) {
      for (let i = 0; i < 10; i++) {
        myArr.push({
          number: Math.floor(Math.random() * 10) + 1,
          clicked: false,
        });
      }
    }
    setNumArr(myArr);
    console.log(myArr);
  }, []);

  return (
    <main className='main'>
      <div className='dice'>
        <Die value='1' />
        <Die value='2' />
        <Die value='3' />
        <Die value='4' />
        <Die value='5' />
        <Die value='6' />
        <Die value='1' />
        <Die value='1' />
        <Die value='1' />
        <Die value='1' />
      </div>
    </main>
  );
}

export default App;
