import React, { useState, useEffect } from 'react';

const Die = (props) => {
  const styles = {
    backgroundColor: props.isHeld ? '#ffecef' : '#ffffff',
  };

  return (
    <div onClick={props.holdDie} className='die' style={styles}>
      <h2 className='die-num'>{props.value}</h2>
    </div>
  );
};

export default Die;
