import React, { useState, useEffect } from 'react';
import Die from './components/Die.jsx';
import { nanoid } from 'nanoid';

function App() {
  const [dice, setDice] = useState(allNewDice());

  function allNewDice() {
    const numArr = [];
    for (let i = 0; i < 10; i++) {
      numArr.push({
        value: Math.ceil(Math.random() * 6),
        isHeld: false,
        id: nanoid(),
      });
    }
    return numArr;
  }

  function reroll() {
    setDice((prevDice) =>
      prevDice.map((die) =>
        die.isHeld ? die : { ...die, value: Math.ceil(Math.random() * 6) }
      )
    );
  }

  function holdDie(id) {
    setDice((prevDice) =>
      prevDice.map((die) =>
        die.id === id ? { ...die, isHeld: !die.isHeld } : die
      )
    );
  }

  return (
    <main className='main'>
      <div className='dice'>
        {dice.map((die) => (
          <Die
            isHeld={die.isHeld}
            value={die.value}
            key={die.id}
            holdDie={() => holdDie(die.id)}
          />
        ))}
      </div>
      <button onClick={() => reroll()} className='roll-dice'>
        Roll
      </button>
    </main>
  );
}

export default App;
