import React, { useState, useEffect } from 'react';
import Die from './components/Die.jsx';
import ScoreBoard from './components/ScoreBoard.jsx';
import { nanoid } from 'nanoid';

function App() {
  const [dice, setDice] = useState(allNewDice());
  const [score, setScore] = useState(0);
  const [tenzies, setTenzies] = useState(false);
  const [firstClick, setFirstClick] = useState(0);
  const [player, setPlayer] = useState({ name: '', saved: false });
  const [scoreBoard, setScoreBoard] = useState(
    () => JSON.parse(localStorage.getItem('scores')) || []
  );

  useEffect(() => {
    // newScore()
    localStorage.setItem('scores', JSON.stringify(scoreBoard));
  }, [scoreBoard]);

  useEffect(() => {
    let dupe = 0;
    for (let i = 0; i < dice.length; i++) {
      if (dice[i].isHeld) {
        dupe += 1;
      }
    }
    dupe === 0 && setFirstClick(0);
    dupe === 10 ? setTenzies(true) : setTenzies(false);
  }, [dice]);

  function allNewDice() {
    const numArr = [];
    for (let i = 0; i < 10; i++) {
      numArr.push({
        value: Math.ceil(Math.random() * 6),
        isHeld: false,
        id: nanoid(),
      });
    }
    return numArr;
  }

  function reroll() {
    if (!tenzies) {
      setScore((prevScore) => prevScore + 1);
      setDice((prevDice) =>
        prevDice.map((die) =>
          die.isHeld ? die : { ...die, value: Math.ceil(Math.random() * 6) }
        )
      );
    } else {
      setScore(0);
      setFirstClick(0);
      setDice(allNewDice());
    }
  }

  function holdDie(myDie) {
    if (firstClick === 0 || firstClick === myDie.value) {
      setFirstClick(myDie.value);
      setDice((prevDice) =>
        prevDice.map((die) =>
          die.id === myDie.id ? { ...die, isHeld: !die.isHeld } : die
        )
      );
    }
  }

  function saveName(e) {
    const { value } = e.target;
    setPlayer((prevPlayer) => ({ ...prevPlayer, name: value }));
    console.log(player);
    e.preventDefault();
  }

  // async function newScore()

  return (
    <div>
      <h1 className='title'>Tenzies</h1>
      <main className='main'>
        <p className='instructions'>
          Roll until all dice are the same. Click each die to freeze it at its
          current value between rolls.
        </p>
        <div className='dice'>
          {dice.map((die) => (
            <Die
              isHeld={die.isHeld}
              value={die.value}
              key={die.id}
              holdDie={() => holdDie(die)}
            />
          ))}
        </div>
        <button onClick={() => reroll()} className='roll-dice'>
          {tenzies ? 'Play Again?' : 'Roll'}
        </button>
        {!player.saved ? (
          <form>
            <input
              type='text'
              placeholder='Enter Name'
              id='enterName'
              className='player--input'
              value={player.name}
              name='topText'
            ></input>
            <button onClick={() => saveName()}>Save Name</button>
          </form>
        ) : (
          <div className='score'>
            <h4>{tenzies ? 'Final ' : ''}Score</h4>
            <h3>{score}</h3>
          </div>
        )}
      </main>
      <br />
      {tenzies && (
        <div className='scoreboard'>
          <ScoreBoard scoreBoard={scoreBoard} />
        </div>
      )}
    </div>
  );
}

export default App;
